import { fromJS } from 'immutable';
import axios from 'axios';

export const updateList = (title, content) => {
  return (dispatch) => {
    const action = fromJS({
      type: 'UPDATE_LIST',
      title,
      content
    })
    dispatch(action);
  }
}
