import styled from 'styled-components';

export const HomeWrapper = styled.div`
  overflow: hidden;
  width: 960px;
  margin: 0 auto;
`
export const HomeLeft = styled.div`
  float: left;
  width: 625px;
  margin-left: 15px;
  padding-top: 30px;
  .banner-img{
    width: 625px;
    height: 240px;
  }
`
export const HomeRight = styled.div`
  float: right;
  width: 240px;
`
