import React, { Component } from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, SearchWrapper, NavSearch, 
				 Addition, Button, SearchInfo, SearchInfoTitle, SearchInfoSwitch,
				 SearchInfoList, SearchInfoItem  
} from './style';
import { CSSTransition } from 'react-transition-group';
import { connect } from 'react-redux';
import { actionCreators } from './store';

class Header extends Component {
	render() {
		const { focused, list, page, totalPage, mouseIn, handleInputFocus, handleInputBlur, handleMouseEnter, 
						handleMouseLeave, handlePageChange } = this.props;
		const newList = list.toJS();
		const resList = []
		const getListArea = () => {
			for(let i = (page - 1) * 10; i < page * 10; i++) {
				resList.push(
					<SearchInfoItem key={newList[i]}>{newList[i]}</SearchInfoItem>
				);
			}
			if(focused || mouseIn) {
				return (
					<SearchInfo onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
						<SearchInfoTitle>
							热门搜索
							<SearchInfoSwitch onClick={handlePageChange.bind(this, page, totalPage)}
							>换一换</SearchInfoSwitch>
						</SearchInfoTitle>
						<SearchInfoList>
							{resList}
						</SearchInfoList>
					</SearchInfo>
				)
			}else {
				return null;
			}
		}

		return (
			<HeaderWrapper>
				<Logo />
				<Nav>
					<NavItem className='left active'>首页</NavItem>
					<NavItem className='left'>下载App</NavItem>
					<NavItem className='right'>登录</NavItem>
					<NavItem className='right'>
						<span className='iconfont'>&#xe636;</span>
					</NavItem>
					<SearchWrapper>
						<CSSTransition
							classNames='slide'
							timeout={200}
							in={focused}
						>
							<NavSearch
								className={focused ? 'focused' : ''}
								onFocus={handleInputFocus}
								onBlur={handleInputBlur}
							></NavSearch>
						</CSSTransition>
						<span 
							className={focused ? 'focused iconfont' : 'iconfont'}
						>&#xe603;</span>
						{getListArea(focused)}
					</SearchWrapper>
				</Nav>
				<Addition>
					<Button className='writting'>
						<span className='iconfont'>&#xe878;</span>
						<span>写文章</span>
					</Button>
					<Button className='reg'>注册</Button>
				</Addition>
		</HeaderWrapper>
		)
	}
}

const mapStateToProps = (state) => {
	return {
		focused: state.get('header').get('focused'),
		list: state.get('header').get('list'),
		page: state.get('header').get('page'),
		totalPage: this.state.get('header').get('totalPage'),
		mouseIn: state.get('header').get('mouseIn')
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		handleInputFocus() {
			dispatch(actionCreators.getList())
			dispatch(actionCreators.inputFocus());
		},
		handleInputBlur() {
			dispatch(actionCreators.inputBlur());
		},
		handleMouseEnter() {
			dispatch(actionCreators.mouseEnter());
		},
		handleMouseLeave() {
			dispatch(actionCreators.mouseLeave());
		},
		handlePageChange() {
			dispatch(actionCreators.pageChange());
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
