import React from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, SearchWrapper, NavSearch, 
				 Addition, Button, SearchInfo, SearchInfoTitle, SearchInfoSwitch,
				 SearchInfoList, SearchInfoItem  
} from './style';
import { CSSTransition } from 'react-transition-group';
import { connect } from 'react-redux';
import { actionCreators } from './store';

const Header = (props) => {
	return (
		<HeaderWrapper>
			<Logo />
			<Nav>
				<NavItem className='left active'>首页</NavItem>
				<NavItem className='left'>下载App</NavItem>
				<NavItem className='right'>登录</NavItem>
				<NavItem className='right'>
					<span className='iconfont'>&#xe636;</span>
				</NavItem>
				<SearchWrapper>
					<CSSTransition
						classNames='slide'
						timeout={200}
						in={props.focused}
					>
						<NavSearch
							className={props.focused ? 'focused' : ''}
							onFocus={props.handleInputFocus}
							onBlur={props.handleInputBlur}
						></NavSearch>
					</CSSTransition>
					<span 
						className={props.focused ? 'focused iconfont' : 'iconfont'}
					>&#xe603;</span>
					<SearchInfo>
						<SearchInfoTitle>
							热门搜索
							<SearchInfoSwitch>换一换</SearchInfoSwitch>
						</SearchInfoTitle>
						<SearchInfoList>
							<SearchInfoItem>教育</SearchInfoItem>
							<SearchInfoItem>教育</SearchInfoItem>
							<SearchInfoItem>教育</SearchInfoItem>
							<SearchInfoItem>教育</SearchInfoItem>
							<SearchInfoItem>教育</SearchInfoItem>
							<SearchInfoItem>教育</SearchInfoItem>
							<SearchInfoItem>教育</SearchInfoItem>
						</SearchInfoList>
					</SearchInfo>
				</SearchWrapper>
			</Nav>
			<Addition>
				<Button className='writting'>
					<span className='iconfont'>&#xe878;</span>
					<span>写文章</span>
				</Button>
				<Button className='reg'>注册</Button>
			</Addition>
	</HeaderWrapper>
	)
}

const mapStateToProps = (state) => {
	return {
		focused: state.get('header').get('focused')
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		handleInputFocus() {
			dispatch(actionCreators.inputFocus());
		},
		handleInputBlur() {
			dispatch(actionCreators.inputBlur());
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
