import { fromJS } from 'immutable';

const defaultState = fromJS({
  topicList: [],
  articleList: [],
  recommendList: []
})

// eslint-disable-next-line import/no-anonymous-default-export
export default (state = defaultState, action) => {
  switch(action.type) {
    case 'GET_WHOLE_PAGE': 
      return (
        state.merge({
          topicList: action.source.get('topicList')
        })
      )
    default: 
      return state;
  }
}
