import * as actionTypes from './actionTypes';

const defaultState = {
  focused: false
}

// eslint-disable-next-line import/no-anonymous-default-export
export default (state = defaultState, action) => {
  if(action.type === 'input_focus'){
    return {
      focused: true,
    }
  }
  if(action.type === 'input_blur') {
    return {
      focused: false,
    }
  }

  return state;
}
