import axios from 'axios';
import { fromJS } from 'immutable';

const initAction = (res) => ({
  type: 'GET_WHOLE_PAGE',
  source: res.data.data,
});

const initLoad = (res) => ({
  type: 'GET_LOAD_MORE',
  source: res.data.data,
})

export const getWholePageFromCreator = () => {
  return (dispatch) => {
    axios.get('/api/home.json').then((res) => {
      const action = initAction(res);
      dispatch(action) ; 
    })
  }
};

export const getLoadMoreInfo = () => {
  return (dispatch) => {
    axios.get('/api/homeLoadMore.json').then((res) => {
      const action = initLoad(res);
      dispatch(action) ; 
    })
  }
}
