import axios from 'axios';

const initAction = () => {

}

export const getWholePageFromCreator = () => {
  return (dispatch) => {
    axios.get('/api/home.json').then((res) => {
      const action = {
        type: 'GET_WHOLE_PAGE',
        source: res.data.data,
      }
      dispatch(action) ; 
    })
  }
}
