import { fromJS } from 'immutable';
import axios from 'axios';

export const updateList = () => {
  return (dispatch) => {
    axios.get('/api/detail.json').then((res) => {
      const action = fromJS({
        type: 'UPDATE_LIST',
        title: res.data.title,
        content: res.data.content
      })
     console.log(action);
    })
  }
}
