import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actionCreators } from '../../../common/header/store';
import { ListItem, ListInfo, LoadMore } from '../style';

class List extends Component {

  render() {
    const { list, handleLoadMore } = this.props;
    return (
      <div>
        {
          list.map( (item) => {
            return(
              <ListItem key={item.get('id')}> 
                <ListInfo>
                  <h3 className='title'>{item.get('title')}</h3>
                  <p className='content'>{item.get('desc')}</p>
                </ListInfo>
                <img alt='' className='list-pic' src={item.get('imgUrl')}/>
              </ListItem>
            )
          } )
        }
        <LoadMore onClick={handleLoadMore}>更多文字</LoadMore>
      </div>

    )
  }
}  

const mapState = (state) => ({
  list: state.get('home').get('articleList')
})

const mapDispatch = (dispatch) => ({
  handleLoadMore() {
    const action = actionCreators.getLoadMoreInfo();
    dispatch(action);
  }
})

export default connect(mapState, mapDispatch)(List);
