import { fromJS } from 'immutable';
import axios from 'axios';

const initAction = (res) => {
  return () => ({
    type: 'UPDATE_LIST',
    title: res.data.title,
    content: res.data.content
  })
}

export const updateList = () => {
  return (dispatch) => {
    axios.get('/api/detail.json').then((res) => {
      const action = {
        type: 'UPDATE_LIST',
        title: res.data.title,
        content: res.data.content
      }
      console.log(action);
      dispatch(action);
    })
  }
}
