const defaultState = {
    focused: false
}

// eslint-disable-next-line import/no-anonymous-default-export
export default (state = defaultState, action) => {
  if(action.type === 'input_focused'){
    return {
      focused: true,
    }
  }
  if(action.type === 'input_blured') {
    return {
      focused: false,
    }
  }

  return state;
}
