import { fromJS } from 'immutable';

const defaultState = fromJS({
  topicList: [],
  articleList: [],
  recommendList: []
})

// eslint-disable-next-line import/no-anonymous-default-export
export default (state = defaultState, action) => {
  switch(action.type) {
    case 'GET_WHOLE_PAGE': 
      return (
        state.merge({
          topicList: fromJS(action.source).get('topicList'),
          articleList: fromJS(action.source).get('articleList'),
          recommendList: fromJS(action.source).get('recommendList'),
        })
      )
    case 'GET_LOAD_MORE':
      return(
        state.set('articleList', state.get('articleList').concat(fromJS(action.source).get('articleList')))
      )
    default: 
      return state;
  }
}
